labsAcf.css:
@import url(https://fonts.googleapis.com/css?family=Arvo:700);
:root{
  --x:0;
  --y:0;
  --px:0;
  --py:0;
}
body{
  background: #eef;
  width: 100%;
  height: 100%;
  overflow: hidden;
  font-family: "Arvo";
  
}
.light{
  width: 300px;
  height: 300px;
  transform:translateZ(0);
  filter: blur(100px);
  position: absolute;
  top: calc(var(--y) - 150px);
  left: calc(var(--x) - 150px);
  background: rgba(255,255,255,.5);
  border-radius: 50%;
  z-index: 2;
}
.container{
  perspective-origin: var(--px) var(--py);
  perspective: 200px;
  width: 100%;
  height: 100%;
  position:absolute;
  
  div{
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-size: 100px;
    color: rgba(0,0,0,.05);
    @for $i from 2 through 15 {
      &:nth-child(#{$i}){
        transform:translate(-50%, -50%) translateZ(#{-$i}px) scale(#{1+$i * .01});
        opacity:#{1 - $i/15};
      }
    }
    &:first-child{
      color: #FFF;
      z-index: 1;
    }
  }
}

html,
body {
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-family: "Righteous", cursive;
  background: #582043;
}
#letters {
  font-weight: bold;
  font-size: 72px;
}
#letters span {
  display: inline-block;
  transition: all 0.2s;
  transform-origin: -300px 0;
  color: #ffb3ba;
  text-shadow: 0.05em 0.05em 0 #6b3356;
  margin-right: 8px;
  opacity: 0;
}

#letters span.space {
  margin-right: 18px;
}
button {
  margin-top: 60px;
  background: transparent;
  border: 2px solid #ffb3ba;
  color: #ffb3ba;
  padding: 8px 16px;
  cursor: pointer;
  border-radius: 5px;
  transition: background 0.2s;
}
button:hover {
  background: rgba(255, 255, 255, 0.1);
}
button:active {
  background: rgba(255, 255, 255, 0.2);
}
button:focus {
  outline: none;
}

#letters span:after {
  content: attr(data-shadow);
  position: absolute;
  top: 0.2em;
  left: 0.1em;
  z-index: -1;
  text-shadow: none;
  background-color: rgba(0, 0, 0, 0.2);
  background-size: 0.05em 0.05em;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

labsAcf.js:
let elem = document.querySelector('#letters')
let rerun = document.querySelector('#rerun')
let sentence = elem.innerText
elem.innerText = ""
for (let i = 0; i < sentence.length; i++) {
  if (sentence[i] !== " ")
    elem.innerHTML += "<span class='tween"+i+"' data-shadow='" + sentence[i] +"'>" + sentence[i] +"</span>"
  else
    elem.innerHTML += "<span class='tween"+i+" space'>" + sentence[i] +"</span>"
} 
for (let i = 0; i < sentence.length; i++) {
  TweenLite.fromTo(".tween"+i, 3, {rotation:"90"}, {rotation:"0", delay:i/10, ease: Elastic.easeOut.config(1, 0.3)});
  TweenLite.fromTo(".tween"+i, 1, {opacity:0}, {opacity:1, delay:i/10});
}
rerun.addEventListener('click', function () {
  for (let i = 0; i < sentence.length; i++) {
    TweenLite.fromTo(".tween"+i, 3, {rotation:"90"}, {rotation:"0", delay:i/10, ease: Elastic.easeOut.config(1, 0.3)});
    TweenLite.fromTo(".tween"+i, 1, {opacity:0}, {opacity:1, delay:i/10});
  }
})

