labsAcf.js:
window.onmousemove = function(event) {
  let circles = document.querySelectorAll(".circle").forEach(function(circle) {
    let circleBounds = circle.getBoundingClientRect();
    let x = event.pageX - circleBounds.left - circleBounds.width / 2;
    let y = event.pageY - circleBounds.top - circleBounds.height / 2;
    let rad = Math.atan2(y, x);
    let deg = Math.floor((rad + Math.PI / 4) * (180 / Math.PI));
    circle.style.transform = "rotate(" + deg + "deg)";
    let percent = 50;
    let coef = Math.sqrt(x * x + y * y) - 64;
    if (coef > 100) coef = 100;
    percent = 50 - coef * 40 / 100;
    if (coef < 0) percent = 50;
    circle.style.borderRadius = "50% " + percent + "% 50% 50%";
  });
};

