$blue: #14cac4;
$pink: #fc2e79;
$yellow: #fbb635;
$offset: 5px;
$bezier: cubic-bezier(0.680, -0.550, 0.265, 1.550);

.loader{
  position: absolute;
  width: 50px;
  height: 50px;
  left: calc(50% - 25px);
  top:calc(50% - 25px);
}
.circle{
  position: absolute;
  top: 0;
  left: 0;
  width: 50px;
  height: 50px;
  border-radius:50%;
  background: $blue;
  z-index: 93;
  mix-blend-mode: multiply;
  box-shadow:0 0 15px 15px $blue;
  animation: 2s loop1 infinite $bezier;
  
  &:nth-child(2){
    background: $pink;
  box-shadow:0 0 15px 15px $pink;
    z-index: 92;
    animation: 2s loop2 infinite $bezier;
  }
  &:nth-child(3){
    background: $yellow;
  box-shadow:0 0 15px 15px $yellow;
    z-index: 91;
    animation: 2s loop3 infinite $bezier;
  }
}
.loader{
    animation: .5s loop4 infinite linear;
}
@keyframes loop4{
  0%{
    transform:rotate(0);
  }
  100%{
    transform:rotate(360deg);
  }
}
@keyframes loop1{
  0%, 100%{
    box-shadow:none;
    transform:translate(0, 0) scale(1) rotate(0deg);
  }
  50%{
    box-shadow:0 0 15px 15px $blue;
    transform:translate(0, -$offset - 5) scale(.5) rotate(20deg);
  }
}
@keyframes loop2{
  0%, 100%{
    box-shadow:none;
    transform:translate(0, 0) scale(1) rotate(200deg);
  }
  50%{
    box-shadow:0 0 15px 15px $pink;
    transform:translate(-$offset, $offset) scale(.5) rotate(10deg);
  }
}
@keyframes loop3{
  0%, 100%{
    box-shadow:none;
    transform:translate(0, 0) scale(1) rotate(140deg);
  }
  50%{
    box-shadow:0 0 15px 15px $yellow;
    transform:translate($offset, $offset) scale(.5) rotate(80deg);
  }
}

