var vcolorpickers = document.querySelectorAll(".vcolorpicker");
var s = 0;
var h = 0;
var l = 1;
var alpha = 1;
vcolorpickers.forEach( elem => {
  console.log(elem, vcolorpickers);
  elem.addEventListener("mousedown", function (event) {
    event.target.querySelector(".vcolorpicker__map_cursor")
      .classList.add("focused");
    if (
      !event.target.querySelector(".vcolorpicker__map_cursor")
        .classList.contains("focused")
    )
      return;
    vcolorpicker_update();
  });
  document.addEventListener("mouseup", function (event) {
    if(document
      .querySelector(".vcolorpicker__map_cursor.focused")) {
    document
      .querySelector(".vcolorpicker__map_cursor.focused")
      .classList.remove("focused");
    }
  });
  document.addEventListener("mousemove", function (event) {
    vcolorpicker_update();
  });
})
function vcolorpicker_update() {
  if (
    document.querySelector(
      ".vcolorpicker__map .vcolorpicker__map_cursor.focused"
    ) != null
  ) {
    var mapRect = document
      .querySelector(".vcolorpicker__map_cursor.focused")
      .parentNode.getBoundingClientRect();
    var x = Math.min(Math.max(event.clientX - mapRect.left, 0), 200);
    var y = Math.min(Math.max(event.clientY - mapRect.top, 0), 200);
    s = x / 200;
    var v = 1 - y / 200;
    l = v - (v * s) / 2;
    document.querySelector(
      ".vcolorpicker__map .vcolorpicker__map_cursor"
    ).style.transform = "translate(" + x + "px, " + y + "px)";
    document.querySelector(
      ".vcolorpicker__map .vcolorpicker__map_cursor"
    ).style.backgroundColor =
      "hsla(" + h + "," + s * 100 + "%," + l * 100 + "%, " + alpha + ")";
  } else if (
    document.querySelector(
      ".vcolorpicker__hue .vcolorpicker__map_cursor.focused"
    ) != null
  ) {
    var mapRect = document
      .querySelector(".vcolorpicker__hue .vcolorpicker__map_cursor.focused")
      .parentNode.getBoundingClientRect();
    var y = Math.min(Math.max(event.clientY - mapRect.top, 0), 200);
    h = Math.round((y / 200) * 360);
    document.querySelector(
      ".vcolorpicker__hue .vcolorpicker__map_cursor"
    ).style.transform = "translateY(" + y + "px)";
    document.querySelector(
      ".vcolorpicker__map .vcolorpicker__map_cursor"
    ).style.backgroundColor =
      "hsla(" + h + "," + s * 100 + "%," + l * 100 + "%, " + alpha + ")";
    document.querySelector(
      ".vcolorpicker__map .vcolorpicker__map_color"
    ).style.background =
      "-webkit-linear-gradient(0deg , hsla(" +
      h +
      ",100%, 50%, 0) 0% , hsla(" +
      h +
      ",100%, 50%, 1) 100%)";
    document.querySelector(
      ".vcolorpicker__opacity .vcolorpicker__huemap"
    ).style.background =
      "-webkit-linear-gradient(0deg , hsla(0, 0%, 0%, 0) 0% , hsla(" +
      h +
      ",100%, 50%, 1) 100% )";
  } else if (
    document.querySelector(
      ".vcolorpicker__opacity .vcolorpicker__map_cursor.focused"
    ) != null
  ) {
    var mapRect = document
      .querySelector(".vcolorpicker__opacity .vcolorpicker__map_cursor.focused")
      .parentNode.getBoundingClientRect();
    var y = Math.min(Math.max(event.clientX - mapRect.left, 0), 200);
    alpha = y / 200;
    document.querySelector(
      ".vcolorpicker__map .vcolorpicker__map_cursor"
    ).style.backgroundColor =
      "hsla(" + h + "," + s * 100 + "%," + l * 100 + "%, " + alpha + ")";
    document.querySelector(
      ".vcolorpicker__opacity .vcolorpicker__map_cursor"
    ).style.transform = "translateX(" + y + "px)";
    document.querySelector(
      ".vcolorpicker__opacity .vcolorpicker__huemap"
    ).style.background =
      "-webkit-linear-gradient(0deg , hsla(0, 0%, 0%, 0) 0% , hsla(" +
      h +
      ",100%, 50%, 1) 100% )";
  }
}

