var ScifiLoader = function(id) {

   this.size = 500
   this.circleNum = 30
   this.mode = "lighter"
   this.particuleNum = 30
   this.circleDelta = 1
   this.particuleDelta = 1
   this.particuleLifeTime = 1
   this.color = [ 0, 128, 255 ];
   this.canvas = document.querySelector(id)
   this.ctx = this.canvas.getContext("2d")
   this.entities = {
      circles: [],
      particules: []
   }

   this.init = () => {
      setInterval(this.run, 20)
   }
   this.run = () => {
      this.update()
      this.render()
   }
   this.update = () => {
      while (this.entities.particules.length < this.particuleNum) {
         this.entities.particules.push(this.generateRandomParticule())
      }
      while (this.entities.circles.length < this.circleNum) {
         this.entities.circles.push(this.generateRandomCircle())
      }
      this.entities.circles.forEach((el, index) => {
         el.update()
         if(el.isDead()) this.entities.circles.splice(index, 1)
      })
      this.entities.particules.forEach((el, index) => {
         el.update()
         if(el.isDead()) this.entities.particules.splice(index, 1)
      })
   }
   this.render = () => {
      this.ctx.globalAlpha = 1
      this.ctx.globalCompositeOperation = "source-over"
      if (this.canvas.width != this.size) {
         this.canvas.width = this.size
         this.canvas.height = this.size
      }
      this.clear()
      this.ctx.globalCompositeOperation = this.mode
      this.getAllEntities().forEach((el) => {
         el.render(this.ctx)
      })
      this.renderCircle(this.ctx);
   }


   this.clear = () => {
      this.ctx.fillStyle="black";
      this.ctx.fillRect(0, 0, this.size, this.size)
   }

   this.getAllEntities = () => {
      return this.entities.circles.concat(this.entities.particules);
   }

   this.generateRandomCircle = () => {
      return new Circle(this.size*3/4 - this.circleDelta + Math.random() * this.circleDelta*2, Math.random()*5, this.color)
   }

   this.generateRandomParticule = () => {
      return new Particle(this.size*3/4 - this.particuleDelta + Math.random() * this.particuleDelta*2, Math.random()*5, this.particuleLifeTime)
   }

   this.renderCircle = (ctx) => {
      ctx.fillStyle = "hsl(0, 100%, 0%)"
      ctx.beginPath()
      ctx.arc(
         ctx.canvas.width / 2,
         ctx.canvas.width / 2,
         this.size*3/4/3 + 10,
         0,
         Math.PI*2
      )
      ctx.fill()
   }

   this.init()
}

//---- Circle.js

class Circle {
   constructor(size,strokeSize) {
      this.size = size
      this.strokeSize = 0
      this.color = Math.random() * 360
      this.opacity = 1
      this.lifeTime = Math.random()*.05 + .01
      this.angle = Math.random()*Math.PI*2
      this.speed = -10+Math.random()*20+2;
      this.type = "circles"
   }
   update() {
      this.opacity -= this.lifeTime
      this.strokeSize = (1-this.opacity+0.2) * 10
      this.angle += this.lifeTime * this.speed
      this.size += this.lifeTime*50
   }
   render(ctx) {
      ctx.strokeStyle = "hsl("+Math.round(this.color)+", 95%, "+Math.round(this.opacity*100)+"%)"
      ctx.lineWidth = this.strokeSize
      ctx.beginPath()
      ctx.lineCap = "round"
      ctx.arc(
         ctx.canvas.width/2,
         ctx.canvas.width/2,
         this.size/3,
         this.angle,
         Math.PI + this.angle
      )
      ctx.stroke()
   }

   isDead() {
      return this.opacity <= 0 || this.size <= 0
   }
}

//---- Particule.js
class Particle {
   constructor(size,color,lt) {
      this.size = size
      this.radius = Math.random() * 10;
      this.strokeSize = 0
      this.color = Math.random() * 360
      this.opacity = 1
      this.lifeTime = Math.random()*.05 + .01
      this.angle = Math.random()*Math.PI*2
      this.speed = Math.random() * 3 * lt + 2;
      this.type = "particules"
      this.x = Math.cos(this.angle)*this.size*3/4/2;
      this.y = Math.sin(this.angle)*this.size*3/4/2;
      this.vx = -1 + Math.random() * 2;
      this.vy = -1 + Math.random() * 2;
   }
   update() {
      this.opacity -= 0.01 * this.speed
      this.radius -= .1 * this.speed
      this.x -= this.vx * this.speed/2
      this.y -= this.vy * this.speed/2
   }
   render(ctx) {
      if(this.radius<0) return;
      ctx.fillStyle = "hsl("+Math.round(this.color)+", 95%, "+Math.round(this.opacity*100)+"%)"
      ctx.beginPath()
      ctx.lineCap = "round"
      ctx.arc(
         this.x + ctx.canvas.width / 2,
         this.y + ctx.canvas.width / 2,
         this.radius,
         0,
         Math.PI*2
      )
      ctx.fill()
   }
   isDead() {
      return this.opacity <= 0 || this.radius <= 0
   }
}


