import * as THREE from "https://cdn.jsdelivr.net/npm/three@0.120.0/build/three.module.js";
import perlinJs from 'https://cdn.skypack.dev/perlin.js';
import { RGBELoader } from 'https://cdn.jsdelivr.net/npm/three@0.120.0/examples/jsm/loaders/RGBELoader.js';

class Example {
  constructor() {
    this.init = this.init.bind(this);
    this.animate = this.animate.bind(this);
    this.onWindowResize = this.onWindowResize.bind(this);
    this.cubes = [];
    this.cubeWidth = 100;
    this.cubePadding = 0;
    this.cubeLength = 20;
    this.cubeOffset = (this.cubeWidth + this.cubePadding) * this.cubeLength / 2;
    this.init();
    this.sceneHeight = 600;
  }

  async init() {
    const envLoader = new RGBELoader();
    const [myenv] = await Promise.all([
      new Promise(res => envLoader.load('https://dl.polyhaven.org/file/ph-assets/HDRIs/hdr/4k/dancing_hall_4k.hdr', res))
    ]);
    this.envMap = myenv;
    this.aspect = window.innerWidth / this.sceneHeight;
    this.camera = new THREE.PerspectiveCamera(25, this.aspect, 1, 3000);
    this.camera.position.z = this.camera.position.x = 600;
    this.camera.position.y = 100;
    this.camera.lookAt(new THREE.Vector3(0, 50, 0));

    for (let x = 0; x < this.cubeLength; x++) {
      for (let y = 0; y < this.cubeLength; y++) {
        let cube = this.newCube(x * (this.cubeWidth + this.cubePadding), y * (this.cubeWidth + this.cubePadding), x / 10, y / 10);
        this.cubes.push(cube);
      }
    }

    this.light = new THREE.AmbientLight(0xAAAAAA, 0.7);
    this.directionalLight = new THREE.DirectionalLight(0x0B0811);
    this.directionalLight.position.set(1, 4, 0.5).normalize();
    this.directionalLight.lookAt(new THREE.Vector3(0, 100, 0));

    this.scene = new THREE.Scene();
    this.scene.background = new THREE.Color(0x0B0811);
    this.scene.fog = new THREE.Fog(0x0B0811, 100, 1400);
    this.cubes.forEach(item => this.scene.add(item.mesh));
    this.scene.add(this.light);
    this.scene.add(this.directionalLight);
    this.scene.environment = myenv;
    this.scene.environment.mapping = THREE.EquirectangularReflectionMapping;

    this.renderer = new THREE.WebGLRenderer({ antialias: true, autoSize: true });
    this.renderer.setPixelRatio(window.devicePixelRatio);
    this.renderer.shadowMap.enabled = true;
    this.renderer.setSize(window.innerWidth, this.sceneHeight);
    document.querySelector("#screen").appendChild(this.renderer.domElement);
    this.camera.rotation.z -= 0.15;
    this.animate();
    this.camera.aspect = window.innerWidth / this.sceneHeight;
    this.camera.updateProjectionMatrix();
    this.renderer.setSize(window.innerWidth, this.sceneHeight);
    this.renderer.render(this.scene, this.camera);
    window.addEventListener("resize", this.onWindowResize);
    setTimeout(() => this.onWindowResize(), 10);
  }

  animate() {
    requestAnimationFrame(this.animate);
    this.cubes.forEach(item => {
      item.position.x += 0.003;
      item.position.y += 0.007;
      item.mesh.position.y = perlinJs.perlin2(item.position.x, item.position.y) * 60;
    });
    this.renderer.render(this.scene, this.camera);
  }

  onWindowResize() {
    this.camera.aspect = window.innerWidth / this.sceneHeight;
    this.camera.updateProjectionMatrix();
    this.renderer.setSize(window.innerWidth, this.sceneHeight);
  }

  newCube(x, y, x1, y1) {
    let geometry = this.createBoxWithRoundedEdges(100, 100, 100, 10, 10);
    let material = new THREE.MeshStandardMaterial({
      color: 0xffffff,
      metalness: 1,
      roughness: 0
    });
    let mesh = new THREE.Mesh(geometry, material);
    mesh.position.x = x - this.cubeOffset;
    mesh.position.z = y - this.cubeOffset;
    return { mesh, position: { x: x1, y: y1 } };
  }

  createBoxWithRoundedEdges(width, height, depth, radius0, smoothness) {
    let shape = new THREE.Shape();
    let eps = 0.00001;
    let radius = radius0 - eps;
    shape.absarc(eps, eps, eps, -Math.PI / 2, -Math.PI, true);
    shape.absarc(eps, height - radius * 2, eps, Math.PI, Math.PI / 2, true);
    shape.absarc(width - radius * 2, height - radius * 2, eps, Math.PI / 2, 0, true);
    shape.absarc(width - radius * 2, eps, eps, 0, -Math.PI / 2, true);
    let geometry = new THREE.ExtrudeBufferGeometry(shape, {
      depth: depth - radius0 * 2,
      bevelEnabled: true,
      bevelSegments: smoothness * 2,
      steps: 1,
      bevelSize: radius,
      bevelThickness: radius0,
      curveSegments: smoothness
    });
    geometry.center();
    return geometry;
  }
}

new Example();

