const scrollSpeed = 5; // Adjust the speed of scrolling
let paused = false;
let direction = 1; // 1 for scroll down, -1 for scroll up
const maxScroll = document.body.scrollHeight - window.innerHeight;

function animateScroll() {
  if(paused) return;
  window.scrollBy(0, direction * scrollSpeed);

  // If the window reaches the bottom, change the direction
  if (window.scrollY + window.innerHeight >= document.body.scrollHeight) {
    direction = -1;
  }
  
  // If the window reaches the top, change the direction
  if (window.scrollY <= 0) {
    direction = 1;
  }

  // Repeat the animation
  requestAnimationFrame(animateScroll);
}

// Start the animation
animateScroll();
window.addEventListener('wheel', function () {
  paused = true;
})

labsAcf.css:
body {
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: #ffce0f;
  overflow: hidden;
}
#cuphead {
  width: 807px;
  height: 553px;
  position: relative;
  transition: all 0.3s ease-in-out;
  & div {
    position: absolute;
    transition: all 0.3s ease-in-out;
  }
  & .head {
    width: 520px;
    height: 340px;
    background: #fff;
    box-shadow: 0 0 0 15px black, inset 15px 0 0 0 #ccc;
    left: 185px;
    top: 178px;
    border-radius: 51% 42% 43% 53% / 17% 16% 77% 77%;
    transform: rotate(-6.5deg);
    z-index: 2;
    overflow: hidden;
    & .mouth-fix {
      width: 316px;
      height: 298px;
      z-index: 7;
      border: 4px solid white;
      top: 13px;
      left: 78px;
      border-radius: 50%;
    }
    & .mouth {
      width: 320px;
      height: 300px;
      background: rgba(black, 1);
      border: 10px solid whiet;
      top: 15px;
      left: 80px;
      overflow: hidden;
      border-radius: 50%;
      &:after {
        content: "";
        position: absolute;
        left: 8px;
        top: 130px;
        width: 110px;
        height: 114px;
        background: rgba(black, 1);
        box-shadow: -20px -40px 0 10px white, -80px -80px 0 50px white;
        border-radius: 50%;
      }
      &:before {
        content: "";
        position: absolute;
        left: 157px;
        top: 212px;
        width: 90px;
        height: 81px;
        background: rgba(black, 1);
        box-shadow: 50px -10px 0 0px white, 110px 45px 0 50px white;
        border-radius: 50%;
      }
      & .mouth-back {
        width: 420px;
        height: 400px;
        background: white;
        top: -181px;
        left: 53px;
        border-radius: 50%;
      }
      & .tongue {
        height: 71px;
        width: 40px;
        top: 216px;
        left: 68px;
        border-radius: 43% 100% / 53% 87% 54% 94%;
        z-index: 6;
        background: #df4418;
        transform: rotate(-48.5deg);
        &:after {
          content: "";
          position: absolute;
          height: 72px;
          width: 43px;
          top: -28px;
          left: 1px;
          border-radius: 43% 100% / 53% 87% 54% 94%;
          z-index: 6;
          background: #df4418;
          transform: rotate(17deg);
          box-shadow: 9px -3px 0 0 black;
        }
      }
    }
    & .nose {
      width: 80px;
      height: 60px;
      background: #df4418;
      top: 160px;
      left: 390px;
      z-index: 10;
      transform: rotate(6.5deg);
      border-radius: 68% 54% 66% 50% / 54% 63% 59% 73%;
      box-shadow: 0 0 0 10px black, -10px 10px 0 10px #eee;
    }
    & .eye-right {
      width: 150px;
      height: 195px;
      top: -10px;
      left: 410px;
      border-radius: 0% 57% 76% 62% / 47% 51% 71% 63%;
      background: #fff;
      box-shadow: 0 0 0 10px black;
      transform: rotate(2deg);
      z-index: 8;
      & .pupil {
        height: 135px;
        width: 60px;
        background: black;
        top: 55px;
        border-radius: 50% 50% 44% 51% / 39% 24% 51% 50%;
        left: 14px;
        &:after {
          width: 2px;
          height: 3px;
          content: "";
          position: absolute;
          z-index: 2;
          right: -20px;
          top: 40px;
          transform: rotate(-4deg);
          border: 25px solid transparent;
          border-right: 50px solid white;
        }
      }
    }
    & .eye-left {
      width: 250px;
      height: 183px;
      top: -10px;
      left: 157px;
      border-radius: 7% 30% 46% 49% / 29% 5% 90% 72%;
      background: #fff;
      box-shadow: 0 0 0 10px black;
      transform: rotate(2deg);
      z-index: 9;
      & .pupil {
        height: 155px;
        width: 80px;
        background: black;
        top: 32px;
        border-radius: 50% 50% 44% 51% / 39% 24% 51% 50%;
        left: 46px;
        &:after {
          width: 2px;
          height: 3px;
          content: "";
          position: absolute;
          z-index: 2;
          right: -5px;
          top: 40px;
          border: 30px solid transparent;
          border-right: 40px solid white;
        }
      }
    }
  }
  & .ance {
    position: absolute;
    top: 350px;
    left: 160px;
    width: 97px;
    height: 90px;
    border-radius: 49% 42% 51% 142%;
    background: transparent;
    box-shadow: 0 0 0 15px black, 0 0 0 25px #ccc, 0 0 0 55px #fff,
      0 0 0 70px black;
    z-index: 1;
  }
  & .straw {
    height: 200px;
    width: 100px;
    top: 63px;
    left: 172px;
    overflow: hidden;
    z-index: 2;
    background: rgba(255, 255, 255, 1);
    transform: rotate(-18deg);
    border-radius: 73% 27% 0% 0% / 50% 7% 0% 0%;
    box-shadow: -15px 20px 0 0 black, 15px -10px 0 0 black;
    &:after {
      position: absolute;
      content: "";
      width: 105%;
      height: 89px;
      border-radius: 50% 40% 0 0;
      top: 50px;
      background: #df4418;
      box-shadow: inset 0 10px 0 0 black;
    }
    &:before {
      position: absolute;
      content: "";
      width: 105%;
      height: 76px;
      border-radius: 50% 40% 0 0;
      top: 100px;
      background: white;
      z-index: 2;
      box-shadow: inset 0 10px 0 0 black;
    }
  }
  & .straw-end {
    height: 200px;
    width: 100px;
    top: -10px;
    left: 74px;
    z-index: 1;
    overflow: hidden;
    background: white;
    transform: rotate(-80deg);
    border-radius: 55% 51% 33% 44% / 34% 14% 0% 43%;
    box-shadow: 0 0 0 15px black;
    &:after {
      position: absolute;
      content: "";
      width: 105%;
      height: 89px;
      border-radius: 50% 50% 0 0;
      top: 50px;
      background: #df4418;
      box-shadow: inset 0 15px 0 0 black;
    }
    &:before {
      position: absolute;
      content: "";
      width: 105%;
      height: 76px;
      border-radius: 50% 50% 0 0;
      top: 100px;
      background: white;
      z-index: 2;
      box-shadow: inset 0 15px 0 0 black;
    }
  }
  &:hover {
    transform: scale(0.9);
    & .eye-left {
      transform: translate(-50px, -50px);
    }
    & .eye-right {
      transform: translate(20px, -50px);
    }
    & .nose {
      transform: translate(-50px, 20px);
    }
    & .mouth,  {
      transform: translate(0, 30px);
    }
    & .pupil,  {
      transform: translate(0, 70px);
    }
    & .mouth-fix{
      transform: translate(0, 30px);
    }
    & .straw {
      transform: translate(30px, -50px) rotate(-18deg);
    }
    & .straw-end {
      transform: translate(-50px, -50px) rotate(-80deg);
    }
    & .ance {
      transform: translate(-50px, 0);
    }
  }
}
